using Simples;
using Simples.SouthAfrican;
using System;
using Xunit;

namespace SimplesTests.SouthAfrican
{
    public class DrivingLicenceTests
    {
        private const string Jon_Hex = "";
        // private const string sampleData = "";

        [Fact]
        public void LoadsObject()
        {
            var dl = DrivingLicence.FromHex(Jon_Hex);
            Assert.Equal("JM", dl.Initials);
            Assert.Equal("PEEL", dl.Surname);
            Assert.Equal("8401295047080", dl.IdNumber);
        }
    }
}
